	(32, 40, 1, 'Hibiskus', 'Pflanze', 0, 0, 0, 0, 0, 10, 0, 10, 10, 0, 100, 0, 0, 'Irgendeine Pflanze mit schönen roten Blüten, wenn sie denn mal blühen !', 'sammelbar', 1),
	(33, 93, 4, 'Schwein', 'Allesfresser', 5, 10, 0, 0, 0, 1, 0, 20, 10, 0, 100, 0, 0, 'Eine Steckdose auf 4 Beinen.', 'angreifbar', 2),
	(34, 39, 4, 'Osterhase', 'Pflanzenfresser', 2, 10, 10, 1, 1, 1, 1, 10, 30, 0, 100, 0, 0, 'Wird häufig mit einem Korb voller Eier gesichtet.', 'angreifbar', 2),
	(35, 245, 4, 'Gorilla', 'Pflanzenfresser', 50, 20, 0, 0, 0, 10, 0, 30, 10, 0, 100, 0, 0, 'So selten wie ein Bigfoot. ', 'angreifbar', 2),
	(36, 41, 3, 'Alligator', 'Fleischfresser', 30, 0, 0, 0, 10, 0, 0, 30, 10, 1, 80, 30, 10, 'Tick Tack ! Ein Maul voller Zähne bedroht dich, zum Glück ohne viel Grips dahinter.', 'angreifbar', 2),
