<?php/* ermittelt den aktuellen Browser des Nutzers und gibt den Browsernamen zurück   -> Behandlung browserspezifischer Eigenheiten */function get_browser_name($user_agent){	if (strpos($user_agent, 'Opera') || strpos($user_agent, 'OPR/')) return 'Opera';	elseif (strpos($user_agent, 'Edge')) return 'Edge';	elseif (strpos($user_agent, 'Chrome')) return 'Chrome';	elseif (strpos($user_agent, 'Safari')) return 'Safari';	elseif (strpos($user_agent, 'Firefox')) return 'Firefox';	elseif (strpos($user_agent, 'MSIE') || strpos($user_agent, 'Trident/7')) return 'Internet Explorer';	return 'Other';}/* Strukturierte Ausgabe von Arrays für Testzwecke */function ausgabe_array($feld, $dim = 1){	foreach ($feld as $element)	{		if ($dim == 1){			echo "[".$element."]";		} else {			echo "[";			ausgabe_array($element, $dim - 1);			echo "]<br>";		}	}}?><script>	//window.onerror = Fehlerbehandlung;    function Fehlerbehandlung (Nachricht, Datei, Zeile) {      Fehler = "Fehlermeldung:\n" + Nachricht + "\n" + Datei + "\n" + Zeile;      zeigeFehler();      return true;    }    function zeigeFehler () {      alert(Fehler);    }	function weiteresElement(letztesElement)	{		const adminForm = document.querySelector('#dvg_admin');		const element_nr = document.querySelector('#' + letztesElement + '_count');		const element_nr_neu = Number(element_nr.value) + 1;		const element_letzt = document.querySelector('#' + letztesElement + '_' + element_nr.value);		var element_neu = element_letzt.cloneNode(true);		element_neu.id = letztesElement+'_'+element_nr_neu;		element_neu.childNodes[1].childNodes[1].id = 'npc_gebiet_auswahl_'+element_nr_neu;		element_neu.childNodes[1].childNodes[1].name = 'npc_gebiet_auswahl_'+element_nr_neu;		element_neu.childNodes[3].childNodes[1].id = 'npc_gebiet_wkt_'+element_nr_neu;		element_neu.childNodes[3].childNodes[1].name = 'npc_gebiet_wkt_'+element_nr_neu;		element_letzt.parentNode.insertBefore(element_neu, element_letzt