<?php/* ermittelt den aktuellen Browser des Nutzers und gibt den Browsernamen zurück   -> Behandlung browserspezifischer Eigenheiten */function get_browser_name($user_agent){	if (strpos($user_agent, 'Opera') || strpos($user_agent, 'OPR/')) return 'Opera';	elseif (strpos($user_agent, 'Edge')) return 'Edge';	elseif (strpos($user_agent, 'Chrome')) return 'Chrome';	elseif (strpos($user_agent, 'Safari')) return 'Safari';	elseif (strpos($user_agent, 'Firefox')) return 'Firefox';	elseif (strpos($user_agent, 'MSIE') || strpos($user_agent, 'Trident/7')) return 'Internet Explorer';	return 'Other';}/* Strukturierte Ausgabe von Arrays für Testzwecke */function ausgabe_array($feld, $dim = 1){	foreach ($feld as $element)	{		if ($dim == 1){			echo "[".$element."]";		} else {			echo "[";			ausgabe_array($element, $dim - 1);			echo "]<br>";		}	}}?>