<?php $nutze_item = explode("_", $_POST["button_inventar"]);$nutzung = $nutze_item[0];if(count($nutze_item) == 2) $nutzung_item_id = $nutze_item[1];	else $nutzung_item_id = 0;if($aktion_spieler->titel == "kampf"){	?>	<a name="benutztes_item"></a>	<p align="center" style="margin-top:20px; margin-bottom:0px; font-size:14pt;">		<font color="red">Ihr seid in einem Kampf und könnt bis zu seinem Ende keine Items benutzen!</font><br />	</p>	<?php} else {	# Item wurde verspeist	if($nutzung == "essen"){		if(items_spieler_aendern($spieler->id, $nutzung_item_id, -1)){			if($benutztes_item = get_item($nutzung_item_id)){				if($benutztes_item->prozent == 0){					$spieler->erholung_punkte($benutztes_item->gesundheit, $benutztes_item->energie, $benutztes_item->zauberpunkte);				} else {					$spieler->erholung_prozent($benutztes_item->gesundheit, $benutztes_item->energie, $benutztes_item->zauberpunkte);				}			}		} else {			echo "Das hätte nicht passieren dürfen.<br />";		}	}	# Item wurde angelegt/abgelegt	if($nutzung == "anlegen"){		if(items_spieler_aendern($spieler->id, $nutzung_item_id, 0, "anlegen")){			if(1==1){				# Item angelegt			}		} else {			?>			<a name="benutztes_item"></a>			<p align="center" style="margin-top:20px; margin-bottom:0px; font-size:14pt;">				<font color="red">An dieser Stelle könnt Ihr nicht noch mehr Items anlegen.</font><br />			</p>			<?php		}	}	if($nutzung == "ablegen"){		if(items_spieler_aendern($spieler->id, $nutzung_item_id, 0, "ablegen")){			if(1==1){				#Item abgelegt			}		} else {			echo "Das hätte nicht passieren dürfen.<br />";		}	}}# Anzeige Tabelle mit vorhandenen Items des Spielersif ($items = get_all_items_spieler($spieler->id)){	?>	<table class="tabelle" style="margin-top:10%;" width="700px" cellpadding="5px">		<tr class="table_tr_kopf">			<td align="center">Item</td>			<td align="center">Bild</td>			<td align="center">Platz</td>			<td align="right">Anzahl</td>			<td align="center">Verwendung</td>		</tr>		<?php		foreach ($items as $item){			?>			<tr class="table_tr_inhalt">				<td align="left" valign="top"><?php echo $item->name ?></td>				<td align="center" valign="top">					<top_mover>						<mover>							<?php							echo $item->beschreibung."<br />";							$item->ausgabe_attribute("inventar");							?>						</mover>						<img src="<?php echo get_bild_zu_id($item->bilder_id) ?>" width="75px" alt=""/>					</top_mover>				</td>				<td align="center" style="min-width:80px;" valign="top"><?php echo $item->slot->name ?></td>				<td align="right" valign="top"><?php echo $item->anzahl ?></td>				<td align="center" valign="top">					<?php					if($item->id == $nutzung_item_id){						echo "<a valign=\"top\" name=\"benutztes_item\" />";					}					if($item->essbar){						?> 						<button class="button_standard" type="submit" alt="Essen" name="button_inventar" value="<?php echo "essen_".$item->id ?>">Essen</button><br />						<?php					}					if($item->ausruestbar){						if($item->slot->angelegt == 0){							?> 							<button class="button_standard" type="submit" alt="Anlegen" name="button_inventar" value="<?php echo "anlegen_".$item->id ?>">Anlegen</button><br />							<?php						} else {							?> 							<button class="button_standard" type="submit" alt="Ablegen" name="button_inventar" value="<?php echo "ablegen_".$item->id ?>">Ablegen</button><br />							<?php						}					}					if($item->verarbeitbar) /*Toll, aber das nützt mir nur was im Handwerksfenster -> Hier keine Anzeige*/;					?>				</td>			</tr>			<?php		}		if(!isset($items[0])){			?>			<tr class="table_tr_inhalt">				<td colspan=4>Keine Items gefunden.</td>			</tr>			<?php		}		?>	</table>	<script type="text/javascript">		window.location = "#benutztes_item";	</script>	<p align="center" style="padding-top:10pt;">		<button class="button_standard" type="submit" name="zurück" value="zurück">zurück</button>	</p>	<?php} else {	echo 